package testapi.common;import java.io.BufferedReader;import java.io.FileReader;import java.util.Properties;import org.apache.log4j.Logger;public  class PropertiesLoader {    private static Logger logger = Logger.getLogger(PropertiesLoader.class);    static Properties  props = new Properties();    String filePath="";    public  PropertiesLoader() {        try {            String config=System.getProperty("TEST_ENV","config");            filePath=System.getProperty("user.dir")+"/config/$default$.properties".replace("$default$",config);            BufferedReader bufferedReader = new BufferedReader(new FileReader(filePath));            props.load(bufferedReader);        } catch (Exception e) {            System.out.println(e);            logger.error(e);        }    }    public static String GetPropertie(String key) {        String value = props.getProperty(key);        return value;    }}